getGridIds : function(index)
{	
	var copyOfGridIds = Array.from(gridIds);
	var gridIndex = index;
	var gridIdsLen = gridIds.length;
	var gridIdVal;

	if(gridIndex > gridIdsLen)
	{
		return null;
	}
	
	while(gridIndex >= 0)
	{	
	if(gridIndex<gridIdsLen)
	{
	    gridIdVal = copyOfGridIds.shift();

	}
	else
	{
		console.log("Grid index not present in the frame");
	}
	gridIndex--;
	}
	return gridIdVal;	
},

