                                                            grid.gridBack.scrollTop=(parseInt(idx)*clHt)
                                                            //alert(idx);
                                                            
                                                            var obj=grid.getRowSelector(parseInt(rowIndex))
                                                            if((obj)||typeof(obj)=="undefined")
                                                                           {
                                                                           //alert(obj);
                                                                           return true
                                                                           }
                                                            else{
                                                            return false}
                                                            }
                                             else
                                                            return null
                                             }
                              }
               }
};              




