                   web.dialog(121,   "/web:dialog_unknown[@text='Enter name of file to save to' or @index='0']").setText(0, "\\\\den00bwk.us.oracle.com\\OATS_ToolsRepository\\OATS_ToolsRepository_"+sBrowser+"\\ScriptResources\\Exportedtemp.csv");
