                                                                              //web.element("//web:div[@id='"+sourceId+"']").focus();
                                                                              //oracle.oats.scripting.modules.functionalTest.common.api.internal.types.Point pt1 = web.element("//web:div[@id='"+sourceId+"']").getElementCenterPoint();
